div#image-slider-container {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 2;
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 100%;
  background-color: rgba(0,0,0,0.9);
  --nav-height-ratio: 85%;
}
div.image-slider-navigation-container {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  height: var(--nav-height-ratio);
}
.image-slider-hidden {
  visibility: hidden;
}
.image-slider-button {
  color: #e2e2e2;
  font-weight: 400;
  font-size: 3em;
  cursor: pointer;
  transition: all 0.1s;
}
.image-slider-button:hover {
  color: #fff;
}
.image-slider-previous-button,
.image-slider-next-button {
  margin: 0 1em;
  font-style: normal;
}
.image-slider-quit-button {
  position: fixed;
  top: 0.2em;
  right: 0.4em;
  z-index: 3;
  font-style: normal;
  line-height: 1em;
}
.image-slider-quit-button:after {
  content: 'x';
}
.image-slider-previous-button:after {
  content: '<';
}
.image-slider-next-button:after {
  content: '>';
}
div.image-slider-image-container {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
}
.image-slider-image-caption,
span.image-slider-no-images {
  margin: 0.5em 0.2em 0;
  color: #fff;
}
.image-slider-image-main {
  max-width: 100%;
  max-height: 100%;
}
div.image-slider-all-images-container {
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 2%;
  height: calc(100% - var(--nav-height-ratio));
}
div.image-slider-all-images-container > img {
  margin: 1px;
  padding: 1%;
  min-width: 0;
  height: 100px;
  cursor: pointer;
}
div.image-slider-all-images-container > img:hover,
img.image-slider-current-image {
  background-color: #fff;
}
@media (max-width: 700px) {
  div#image-slider-container {
    display: block;
    padding: 0;
  }
  div.image-slider-navigation-container {
    justify-content: space-between;
    height: 100%;
  }
  .image-slider-button {
    color: #e2e2e2;
    font-weight: 400;
    font-size: 3em;
    cursor: pointer;
    transition: all 0.1s;
  }
  .image-slider-previous-button,
  .image-slider-next-button {
    margin: 0 0.2em;
  }
  .image-slider-image-caption,
  span.image-slider-no-images {
    display: none;
  }
  .image-slider-image-main {
    max-width: 100%;
    max-height: 100%;
  }
  div.image-slider-all-images-container {
    display: none;
  }
}
